}
else if (switch_1.getActiveConnection() == CROSS_CROSS)
// just 'else' would be enough but this way it is more clear
{
    if (switch_2.getActiveConnection() == CROSS_DIRECT) {
	if (switch_3.getActiveConnection() == CROSS_DIRECT &&  isUlm2Active()) {

	    // highlight connectors
