/**
 * highlight a connector
 */
function highligth(widget) {

    if (lhc.getStatus() == 1) // i.e. the LHC is active
    {
	widget.setLineColor(255, 0, 0);
    }
    else if (rhc.getStatus() == 1) // i.e. the RHC is active
    {
	widget.setLineColor(128, 128, 128);
    }
    else // i.e. neither LHC nor RHC is active, do not highlight anything
    {
	widget.setLineColor(128, 128, 128);
    }
}



/**
 * remove highlighting on a connector
 */
function unhighligth(widget) 
{
    if (lhc.getStatus() == 1) // i.e. the LHC is active
    {
	widget.setLineColor(128, 128, 128);
    }
    else if (rhc.getStatus() == 1) // i.e. the RHC is active
    {
	widget.setLineColor(255, 0, 0);
    }
    else // i.e. neither LHC nor RHC is active, do not highlight anything
    {
	widget.setLineColor(128, 128, 128);
    }
}


/**
 * highlight a Switch
 * widget: the Switch to highlight
 * pole the pole number to highlight (1 or 2)
 */
function highlightSwitch(widget, pole) 
{
    
    if (lhc.getStatus() == 1) // i.e. the LHC is active
    {
	    if (pole == 1)  
	    {
		widget.getPole1().setLineColor(255, 0, 0);
		widget.getPole2().setLineColor(128, 128, 128);
	    }
	    else if (pole == 2) 
	    {
		widget.getPole2().setLineColor(255, 0, 0);
		widget.getPole1().setLineColor(128, 128, 128);
	    }
    }
    else if (rhc.getStatus() == 1) // i.e. the RHC is active
    {
	    if (pole == 1)  
	    {
		widget.getPole2().setLineColor(255, 0, 0);
		widget.getPole1().setLineColor(128, 128, 128);
	    }
	    else if (pole == 2) 
	    {
		widget.getPole1().setLineColor(255, 0, 0);
		widget.getPole2().setLineColor(128, 128, 128);
	    }
    }
    else // i.e. neither LHC nor RHC is active, do not highlight anything
    {
      //
    }
    

}

/**
 * Un-highlight a Switch
 * widget: the Switch to highlight
 * pole the pole number to highlight (1 or 2)
 */
function unhighlightSwitch(widget, pole) 
{
    if (pole == 1)  
    {
	widget.getPole1().setLineColor(128, 128, 128);
    }
    else if (pole == 2) 
    {
	widget.getPole2().setLineColor(128, 128, 128);
    }
}

/**
 * remove highlighting on all connectors
 */
function unhighligthAll() 
{
    connUp_1.setLineColor(128, 128, 128);
    connUp_2.setLineColor(128, 128, 128);
    connUp_3.setLineColor(128, 128, 128);
    connUp_4.setLineColor(128, 128, 128);
    connUp_5.setLineColor(128, 128, 128);
    connUp_6.setLineColor(128, 128, 128);

    connDown_1.setLineColor(128, 128, 128);
    connDown_2.setLineColor(128, 128, 128);
    connDown_3.setLineColor(128, 128, 128);
    connDown_4.setLineColor(128, 128, 128);
    connDown_5.setLineColor(128, 128, 128);
    connDown_6.setLineColor(128, 128, 128);
    
    // unhighlight switches too
    switch_1.getPole1().setLineColor(128, 128, 128);
    switch_2.getPole1().setLineColor(128, 128, 128);
    switch_3.getPole1().setLineColor(128, 128, 128);
    
    switch_1.getPole2().setLineColor(128, 128, 128);
    switch_2.getPole2().setLineColor(128, 128, 128);
    switch_3.getPole2().setLineColor(128, 128, 128);
    
}


/**
 * Check the status of the widget and return true if the status is 1, false
 * otherwise.
 */
function isActive(widget) {
    if (widget.getStatus() == 1) {
	return true;
    }
    else {
	return false;
    }
}

/**
 * Check if the ULM 1 is active. Return true if it is, false otherwise.
 * (swap ULM 1 with 2 if the RHC is selected) 
 */
function isUlm1Active() {
    
    var isActive = false; 
    if (lhc.getStatus() == 1) // i.e. the LHC is active
    {
	isActive = (ulm1.getStatus() == 1);
    }
    else if (rhc.getStatus() == 1) // i.e. the RHC is active
    {
	isActive = (ulm2.getStatus() == 1);
    }
    
    return isActive;
}


/**
 * Check if the ULM 2 is active. Return true if it is, false otherwise.
 * (swap ULM 1 with 2 if the RHC is selected) 
 */
function isUlm2Active() { 
     
    var isActive = false; 
    if (lhc.getStatus() == 1) // i.e. the LHC is active
    {
	isActive = (ulm2.getStatus() == 1);
    }
    else if (rhc.getStatus() == 1) // i.e. the RHC is active
    {
	isActive = (ulm1.getStatus() == 1);
    }
    
    return isActive;
}

